import React, { useState,useEffect, useRef } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import Chartjs from 'chart.js';
import moment from 'moment';
import { ThemeConsumer } from 'react-bootstrap/esm/ThemeProvider';


const url = 'http://api.openweathermap.org/data/2.5/forecast?q=urbana&appid=4ccd9d42e589371f8ea88443f33536f9&units=imperial'


const Chart = () => {
  const chartContainer = useRef(null);
  const [chartInstance, setChartInstance] = useState(null);

  const test = useState({unit: 20})
  const test2 = useState({unit: moment().format('dddd')})


  useEffect(() => {
    async function get() {

        const response = await fetch(url);
        const data = await response.json();
    
        console.log(data.list[0].main.temp)    
        test[1]({unit: data.list[0].main.temp})

    }

    fetch(url, {method: 'GET',})
      .then((response) => {test[1]({unit: response.list[1].main.temp})})
      .then((response) => response.json())
      .then((responseJson) => {testHandler(responseJson);})

    
    get();

  }, [chartContainer]);




  const chartConfig = {
    type: 'line',
    data: {
      datasets: [{
          data: [test[0].unit, 42, 40, 30, 35, 40, 43],
          label: 'Temperture Forcast (C)',
          backgroundColor: '#fde17e',
  
          // This binds the dataset to the left y axis
          yAxisID: 'left-y-axis'
      }, {
          data: [0.10, 0.20, 0.30, 0.30, 0.30, 0, 0.20],
          label: 'Chance of Rain (%)',
          backgroundColor: '#4DC3FF',
  
          // This binds the dataset to the right y axis
          yAxisID: 'right-y-axis'
      }],
      labels: [test2[0].unit,'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
  },
    options: {
          scales: {
              yAxes: [{
                  id: 'left-y-axis',
                  type: 'linear',
                  position: 'left'
              }, {
                  id: 'right-y-axis',
                  type: 'linear',
                  position: 'right'
              }]
          }
      }
  };




  const testHandler = (e) => {

    console.log('it worked');
    console.log(e);
    if (chartContainer && chartContainer.current) {
        const newChartInstance = new Chartjs(chartContainer.current, chartConfig);
        setChartInstance(newChartInstance);
    }
  }





  return (
    <Container>
        <Row>
            <Col sm={4} ></Col>
            <Col>
                <h4>Weather App</h4>
                <button onClick={testHandler} >test</button>
            </Col>
        </Row>
        <Row>
            <Col>
                <canvas ref={chartContainer} />
            </Col>
        </Row>
    </Container>
  );
};

export default Chart;
