import React, {useState} from 'react'
import { Card, Container, Button, Row, Col } from 'react-bootstrap';
import Bird from './pic-1.jpg';
import Bir from './pic-2.jpg';
import Bi from './pic-3.jpg';

function Routes({showRoutes, salt, sangamon, saline}) {
    
    const salineLevel = useState({level: 'loading...'})
    const saltForkLevel = useState({level: 'loading...'})
    const sangamonLevel = useState({level: 'loading...'})


    const routeData = useState({routeCards: [
        {title: 'Salt Fork', body: 'It is a wide river', img: Bird, button: salt, waterLevel:  saltForkLevel[0].level},
        {title: 'Sangamon', body: 'This river is Lincolns river', img: Bir, button: sangamon, waterLevel:  sangamonLevel[0].level},
        {title: 'Saline', body: 'This is the narrowest and most likly to see some cool animals', img: Bi, button: saline, waterLevel:  salineLevel[0].level},
    ]})

    let card = null

    const salineURL = 'https://waterservices.usgs.gov/nwis/iv/?format=json&sites=03337570&parameterCd=00065&siteStatus=all'
    const saltForkURL = 'https://waterservices.usgs.gov/nwis/iv/?format=json&sites=03336900&parameterCd=00065&siteStatus=all'
    const sangamonURL = 'https://waterservices.usgs.gov/nwis/iv/?format=json&sites=05572000&parameterCd=00065&siteStatus=all'

    const testHandler = () => {
        console.log('test')
        // card = (
        //     <Row>
        //         {routeData[0].routeCards.map(card => {
        //             return (
        //                 <Col sm={4}>
        //                     <Card.Img variant="top" src={card.img} />
        //                     <Card.Body className='kayaks-8'>
        //                         <Card.Title>{card.title}</Card.Title>
        //                         <Card.Text>
        //                             <p>{card.body}</p>
        //                             <h6>Water Level: {} ft</h6>
        //                         </Card.Text>
        //                         <Button onClick={() => {setTimeout(testHandler, 2000)}} variant="primary">Book Trip</Button>
        //                     </Card.Body>
        //                 </Col>
        //         )})}
        //     </Row>
        // )
    }


    const getDataHandler = () => {
        async function getData() {
            const response = await fetch(salineURL);
            const responseSalt = await fetch(saltForkURL);
            const responseSangamon = await fetch(sangamonURL);
            const data = await response.json();
            const dataSalt = await responseSalt.json();
            const dataSangamon = await responseSangamon.json();
            const salineWaterLevel = data.value.timeSeries[0].values[0].value[0].value
            const saltForkWaterLevel = dataSalt.value.timeSeries[0].values[0].value[0].value
            const sangamonWaterLevel = dataSangamon.value.timeSeries[0].values[0].value[0].value
            salineLevel[1]({level: salineWaterLevel})
            saltForkLevel[1]({level: saltForkWaterLevel})
            sangamonLevel[1]({level: sangamonWaterLevel})









        }
    
        getData();

    };

    let WaterLevel = null
    const setClassNameHandler = () => {
        WaterLevel = 'kayaks-0'
    };




    if (true) {
        // testHandler();
        
        setTimeout(
            () => testHandler(), 
            1000
        );

    }

    // if (true) {
    //     card = (
    //         <Row>
    //             {routeData[0].routeCards.map(card => {
    //                 return (
    //                     <Col sm={4}>
    //                         <Card.Img variant="top" src={card.img} />
    //                         <Card.Body className='kayaks-8'>
    //                             <Card.Title>{card.title}</Card.Title>
    //                             <Card.Text>
    //                                 <p>{card.body}</p>
    //                                 <h6>Water Level: {} ft</h6>
    //                             </Card.Text>
    //                             <Button onClick={() => {setTimeout(testHandler, 2000)}} variant="primary">Book Trip</Button>
    //                         </Card.Body>
    //                     </Col>
    //             )})}
    //         </Row>
    //     )
    // }



    let routes = null
    if (showRoutes){

        if (saltForkLevel[0].level === 'loading...'){
            getDataHandler()
        }

        if (1 > salineLevel[0].level){
            setClassNameHandler()
        }

        
        routes = (
        <Container>
                {card}
        </Container>)
    } else { routes = null };
    
    
    
    
    return (
        <div>
            {routes}
        </div>
    )
}

export default Routes;
