import React, {useState} from 'react';
import './App.css';
import moment from 'moment';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Componets/Navbar/navbar';
import MyJumbotron from './Componets/Jumbotron/jumbotron';
import { Container, Row, Col } from 'react-bootstrap';
import Routes from './Componets/routes/routes';
import Prices from './Componets/Prices/prices';
import Pickup from './Componets/Pickup/pickup';
import Calendar from './Componets/calendar';
import SelectionFrom from './Componets/SelectionForm/SelectionForm';
import axios from './axios-orders';


export default function App(){

  const showCompent = useState({componets: [
    {jumbotron: true},
    {calendar: false},
    {price: false},
    {routes: true},
    {pickup: false},
    {selectionForm: false},
  ]});
  const selectRoute = useState({route: 'no route selected'});
  const [value , setValue] = useState(moment());
  const selectName = useState({name: 'no name'})
  const numOfKayaks = useState({numOfKayaks: '0'})
  const timeOfDay = useState({time: 'not picked'})


  const showCalendar = () => {showCompent[1]({componets: [{jumbotron: false}, {calendar: true}, {price: false}, {routes: false}, {pickup: false}, {selectionForm: false},]})}
  const showHomeHandler = () => {showCompent[1]({componets: [{jumbotron: true}, {calendar: false}, {price: false}, {routes: true}, {pickup: false}, {selectionForm: false},]})}
  const showSelectionFormHandler = () => {showCompent[1]({componets: [{jumbotron: false}, {calendar: false}, {price: false}, {routes: false}, {pickup: false}, {selectionForm: true},]})}

  //selecting the booking Data
  const saltHandler = () => {selectRoute[1]({route: 'Salt Fork'});showCalendar()}
  const salineHandler = () => {selectRoute[1]({route: 'Saline'});showCalendar()}
  const sangamonHandler = () => {selectRoute[1]({route: 'Sangamon'});showCalendar()}
  const pickingNameHandler = (e) => {selectName[1]({name: e.target.value})}
  const pickingKayaksHandler = (e) => {numOfKayaks[1]({numOfKayaks: e.target.value})}
  const pickingTimeOfDayHandler = (e) => {timeOfDay[1]({time: e.target.value})}


  const postDataHandler = () => {  
    const booking = {
        name: selectName[0].name,
        numOfKayaks: numOfKayaks[0].numOfKayaks,
        time: timeOfDay[0].time,
        route: selectRoute[0].route,
        date: value.format("MM/DD/YY")
    }
    axios.post('/bookings.json', booking)
      .then(response => console.log(response))
      .catch(err => console.log(err));

    showHomeHandler()  
  }


  //checking what componet to render using showCompent State to verify
  let jumbo = null
  let routes = null
  let calendar = null
  let selectionForm = null
  if (showCompent[0].componets[0].jumbotron) { jumbo = ( <MyJumbotron/>);} else { jumbo = (null) };
  if (showCompent[0].componets[5].selectionForm){ selectionForm = ( 
    <SelectionFrom 
      route={selectRoute[0].route} 
      value={value}
      onChangeName={pickingNameHandler}
      time={pickingTimeOfDayHandler}
      numOfKayaks={pickingKayaksHandler}
      back={showCalendar}
      submit={postDataHandler} />);}
        else { selectionForm = (null)  };
  if (showCompent[0].componets[1].calendar){
    calendar = (
      <Container>
        <Row>What's Available</Row>
        <Row>
          <Col><p className='kayaks-0'>kayaks = 0</p></Col>
          <Col><p className='kayaks-2'>kayaks > 2</p></Col>
          <Col><p className='kayaks-4'>kayaks > 4</p></Col>
          <Col><p className='kayaks-6'>kayaks > 6</p></Col>
          <Col><p className='kayaks-8'>kayaks > 8</p></Col>
        </Row>
        <Calendar form={showSelectionFormHandler} value={value} onChange={setValue} />
      </Container>
    )
  } else { calendar = null };
  if (showCompent[0].componets[3].routes){
    routes = (<Routes 
      saline={salineHandler}
      salt={saltHandler}
      sangamon={sangamonHandler}
    />)
  } else { routes = null };






  //whats rendered to the DOM

  return (
      <div>
          <MyNavbar showHome={showHomeHandler} />
          {jumbo}
          {selectionForm}
          {calendar}
          {routes}          
          <footer className="footer mt-auto py-3">
          <Container>
              <span className="text-muted">Copyright&#169; 2021 KingFisher Kayaking</span>
          </Container>         
          </footer>
      </div>
  )
}
