import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Navbar/navbar';
import MyJumbotron from './Jumbotron/jumbotron';
import Day from './day/day';
import { Container, Row, Col, Button } from 'react-bootstrap';

class App extends Component {
  state = {
    persons: [
      {name: 'jaems', age: 12},
      {name: 'jaesdfms', age: 122}
    ],
    jumbotron: true
  }

  hidJumbotronhandler = () => {
    this.setState({
      persons: [
        {name: 'jaems', age: 12},
        {name: 'jaesdfms', age: 122}
      ],
      jumbotron: !this.state.jumbotron
    })
  }

  render(){

    let jumbo = null

    if (this.state.jumbotron){
      jumbo = (
        <div>
          <MyJumbotron click={this.hidJumbotronhandler}/>
        </div>
      );
    } else {
      jumbo = (
        <Container>
          <Row>
            <Col>
              <h4>Calendar</h4>
            </Col>
            <Col>
              <Button onClick={this.hidJumbotronhandler} >back</Button>
            </Col>
          </Row>
          <Row>
            <Col>        
              <Day/>
            </Col>
            <Col>        
              <Day/>
            </Col>
            <Col>        
              <Day/>
            </Col>
            <Col>        
              <Day/>
            </Col>
            <Col>        
              <Day/>
            </Col>
            <Col>        
              <Day/>
            </Col>
            <Col>        
              <Day/>
            </Col>
          </Row>
        </Container>
      )
    }


    return (
      <div>
        <MyNavbar booking={this.hidJumbotronhandler}/>
        {jumbo}
      </div>
    );
  }
}

export default App;
