import React, {useState, useEffect} from 'react'
import { Container, Row} from 'react-bootstrap';
import firebase from 'firebase';
import './routes.css';
import Route from './routeCard';


function Routes({showRoutes, setRoute, waterLevel}) {

    //firebase requirements
    const database = firebase.database();
    const ref = database.ref('routes');

    const [routesList, setRoutes] = useState([])


    //getting routes list data
    useEffect(() => {
        ref.on('value', gotDataHandler, errDataHandler);
    }, [])

    const gotDataHandler = (data) => {
        const dataRoutes = data.val();
        const keys = Object.keys(dataRoutes);
        for (let i = 0; i < keys.length; i ++){
          let k = keys[i]
          const list = dataRoutes[k]
          setRoutes(routesList => [...routesList, list])
        }      
    }
    const errDataHandler = (err) => {
        console.log('Error!')
        console.log(err)
    }


    let routes = null
    if (showRoutes){        
        routes = (
        <Container className='myRoutes'>
                <Row >
                    {routesList.map(route =>{
                        return <Route className='hover'  waterLevelSetting={waterLevel} key={route.name} setRoute={setRoute} title={route.name} cost={route.cost} duration={route.duration} waterLevel={route.site} pic={route.img}  />
                    })}
                </Row>
                <Row>
                    <NewRoute />
                </Row>
        </Container>)
    } else { routes = null };
    
    
    
    
    return (
        <div>
            {routes}
        </div>
    )
}

export default Routes;
