import React, {useState} from 'react';
import './App.css';

//lib
import moment from 'moment';
import firebase from 'firebase';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'video-react/dist/video-react.css'; // import css
import { Row, Col } from 'react-bootstrap';


//Awesome Fonts
import { library } from '@fortawesome/fontawesome-svg-core'
import { faCheckSquare, faCoffee, faArrowLeft, faArrowRight } from '@fortawesome/free-solid-svg-icons'

//components
import MyNavbar from './Componets/small-componets/Navbar/navbar';
import MyJumbotron from './Componets/small-componets/Jumbotron/jumbotron';
import Routes from './Componets/routes/routes';
import ShowCalendar from './Componets/calendar/showCalendar'
import SelectionFrom from './Componets/SelectionForm/SelectionForm';
import Footer from './Componets/footer';
import Video from './Componets/small-componets/video/video';
import Store from './Componets/Ecwids/Ecwids'

import River from './Componets/rivers/rivers'




export default function App(){

  //Awesome Fonts function
  library.add( faCheckSquare,faArrowLeft, faArrowRight, faCoffee)

  const showCompent = useState({componets: [
    {jumbotron: true},
    {calendar: false},
    {price: false},
    {routes: true},
    {pickup: false},
    {selectionForm: false},
    {showLoading: true},
    {showWaiver: false}
  ]});
  const selectRoute = useState({route: 'no route selected'});
  const [value , setValue] = useState(moment());
  const selectName = useState({name: 'no name'})
  const numOfKayaks = useState({numOfKayaks: '0'})
  const timeOfDay = useState({time: 'not picked'})
  const pickUpLocation = useState({location: 'not picked'})
  const phoneNum = useState({phone: 'not picked'})
  const AlertShow = useState({showAlert: null})
  const showVideo = useState({video: true})
  const showRiver = useState({River: false})
  const wavierArgreedTo = useState({user: false})



  const booked = useState({bookings: [
    {date: '2020/11/23', numOfKayaks: 7},
    {date: '2020/11/24', numOfKayaks: 9},
    {date: '2020/11/26', numOfKayaks: 3},
    {date: '2020/11/18', numOfKayaks: 5},
  ]})

  const database = firebase.database();
  const ref = database.ref('bookings');



  //selecting what should show
  const showCalendar = () => {showCompent[1]({componets: [{jumbotron: false}, {calendar: true}, {price: false}, {routes: false}, {pickup: false}, {selectionForm: false}, {showLoading: false}]});showVideo[1]({video:false});AlertShow[1]({showAlert: null})}
  const showHomeHandler = () => {showCompent[1]({componets: [{jumbotron: true}, {calendar: false}, {price: false}, {routes: true}, {pickup: false}, {selectionForm: false},{showLoading: false}, {showVideo: true}]});showVideo[1]({video:true});AlertShow[1]({showAlert: null});   showRiver[1]({River: false}) }
  const showSelectionFormHandler = () => {showCompent[1]({componets: [{jumbotron: false}, {calendar: false}, {price: false}, {routes: false}, {pickup: false}, {selectionForm: true},{showLoading: false}]})}
  const showWaiverHandler = () =>  {showCompent[1]({componets: [{selectionForm: false},{showWaiver: true}]})}
  const showAlertHandler = () => {AlertShow[1]({showAlert: 2})}
  const showRiversHandler = () => {showRiver[1]({River: true});showCompent[1]({componets: [{jumbotron: false}, {calendar: false}, {price: false}, {routes: false}, {pickup: false}, {selectionForm: false}, {showLoading: false}]});showVideo[1]({video:false});AlertShow[1]({showAlert: null})}

  //selecting the booking Data && 'SelectionForm.js' handlers
  const saltHandler = () => {selectRoute[1]({route: 'Salt Fork'});showCalendar(); showSelectionFormHandler()}
  const salineHandler = () => {selectRoute[1]({route: 'Saline'});showCalendar(); showSelectionFormHandler()}
  const sangamonHandler = () => {selectRoute[1]({route: 'Sangamon'});showCalendar(); showSelectionFormHandler()}
  const pickingNameHandler = (e) => {selectName[1]({name: e.target.value})}
  const pickingKayaksHandler = (e) => {numOfKayaks[1]({numOfKayaks: e.target.value})}
  const pickingTimeOfDayHandler = (e) => {timeOfDay[1]({time: e.target.value})}
  const phoneNumberHandler = (e) => {phoneNum[1]({phone: e.target.value})}
  const setPickUpLocationHandler = (e) => {pickUpLocation[1]({location: e.target.value})}

  const checkboxHandler = (e) => {wavierArgreedTo[1]({user: e.target.checked})}

  const booking = {
    name: selectName[0].name,
    numOfKayaks: numOfKayaks[0].numOfKayaks,
    time: timeOfDay[0].time,
    pickUpLocation: pickUpLocation[0].location,
    route: selectRoute[0].route,
    date: value.format("MM/DD/YY"),
    phone: phoneNum[0].phone
  }




  //post and reading from the firebase database

  const gotDataHandler = (data) => {

    const dataBookings = data.val();
    const keys = Object.keys(dataBookings);

    for (let i = 0; i < keys.length; i ++){
      let k = keys[i]
      const list = dataBookings[k]
      console.log(list.date)
    }

  }

  const errDataHandler = (err) => {
    console.log('Error!')
    console.log(err)
  }

  const onClickHandler = () => {
    console.log(booking)
    ref.push(booking);

  }

  const getDataHandler = () => {

    ref.on('value', gotDataHandler, errDataHandler);
  }


  //whats rendered to the DOM
  return (
      <div>
          <MyNavbar showHome={showHomeHandler} showRivers={showRiversHandler} />
          <River show={showRiver[0].River} />
          <Video showVideo={showVideo[0].video} />
          {/* <Store/> */}
          <MyJumbotron showJumbo={showCompent[0].componets[0].jumbotron} />
          {/* <button onClick={getDataHandler} >get data</button> */}
          <Routes showRoutes={showCompent[0].componets[3].routes} salt={saltHandler} sangamon={sangamonHandler} saline={salineHandler} />
          <Row>
            <Col sm={6}>
              <ShowCalendar
              showCalendar={showCompent[0].componets[1].calendar}
              form={showSelectionFormHandler}
              value={value}
              setValue={setValue}
              back={showHomeHandler}
              booked={booked[0].bookings}
            />
            </Col>
            <Col sm={6}>
                  <SelectionFrom 
                  showForm={showCompent[0].componets[5].selectionForm} 
                  name={pickingNameHandler}
                  route={selectRoute[0].route}
                  value={value}
                  kayaks={pickingKayaksHandler}
                  time={pickingTimeOfDayHandler}
                  phone={phoneNumberHandler}
                  location={setPickUpLocationHandler}
                  submit={onClickHandler}
                  back={showCalendar}
                  clickWaiver={showWaiverHandler}
                  checked={checkboxHandler}
                  showWhichAlert={AlertShow[0].showAlert}
                  booked={booked[0].bookings}
                  AlertShow={AlertShow[0].showAlert}
                />
            </Col>
          </Row>
          <Footer/>
      </div>
  )
}
