import React, {useState, useEffect} from 'react'
import { Card, Container, Button, Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import firebase from 'firebase';
import './routes.css';
import Route from './routeCard';
import Bird from './pic-1.jpg';
import Bir from './pic-2.jpg';
import Bi from './pic-3.jpg';

function Routes({showRoutes, salt, sangamon, saline}) {
    
    const salineLevel = useState({level: '???'})
    const saltForkLevel = useState({level: '???'})
    const sangamonLevel = useState({level: '???'})

    const [routesList, setRoutes] = useState([])


    const database = firebase.database();
    const ref = database.ref('routes');

    //setting       water level has to be at least...
    const waterLevelSetting = 1

    useEffect(() => {
        ref.on('value', gotDataHandler, errDataHandler);
    }, [])



    const gotDataHandler = (data) => {
        let newList = []
        const dataRoutes = data.val();
        const keys = Object.keys(dataRoutes);
        for (let i = 0; i < keys.length; i ++){
          let k = keys[i]
          const list = dataRoutes[k]
        //   newList.push(list)
          setRoutes(routesList => [...routesList, list])
        }      
    }
    const errDataHandler = (err) => {
        console.log('Error!')
        console.log(err)
    }


    const salineURL = 'https://waterservices.usgs.gov/nwis/iv/?format=json&sites=03337570&parameterCd=00065&siteStatus=all'
    const saltForkURL = 'https://waterservices.usgs.gov/nwis/iv/?format=json&sites=03336900&parameterCd=00065&siteStatus=all'
    const sangamonURL = 'https://waterservices.usgs.gov/nwis/iv/?format=json&sites=05572000&parameterCd=00065&siteStatus=all'





    const getDataHandler = () => {
        // async function getData() {
        //     const response = await fetch(salineURL);
        //     const responseSalt = await fetch(saltForkURL);
        //     const responseSangamon = await fetch(sangamonURL);
        //     const data = await response.json();
        //     const dataSalt = await responseSalt.json();
        //     const dataSangamon = await responseSangamon.json();
        //     const salineWaterLevel = data.value.timeSeries[0].values[0].value[0].value
        //     const saltForkWaterLevel = dataSalt.value.timeSeries[0].values[0].value[0].value
        //     const sangamonWaterLevel = dataSangamon.value.timeSeries[0].values[0].value[0].value
        //     salineLevel[1]({level: salineWaterLevel})
        //     saltForkLevel[1]({level: saltForkWaterLevel})
        //     sangamonLevel[1]({level: sangamonWaterLevel})
        // }
    
        // getData();

    };

    const clickHandler = () => {
        console.log(routesList[0].name)
    }

    let title = null
    if(1 > routesList.length ){
        console.log('routes is none')
    } else {
        title = (routesList[0].name)
    }

    let saltClass = 'kayaks-8'
    let sangamonClass = 'kayaks-8'
    let salineClass = 'kayaks-8'
    let saltButton = <Button onClick={salt} variant="primary">Book Trip</Button>
    let sangamonButton = <Button onClick={sangamon} variant="primary">Book Trip</Button>
    let salineButton = <Button onClick={saline} variant="primary">Book Trip</Button>

    const stylingSaltHandler = () => {
        saltClass = 'kayaks-0'
    };
    const stylingSangamonHandler = () => {
        sangamonClass = 'kayaks-0'
    };
    const stylingSalineHandler = () => {
        salineClass = 'kayaks-0'
    };

    let routes = null
    if (showRoutes){

        if (saltForkLevel[0].level === '???'){
            getDataHandler()
        }

        //styling
        if (waterLevelSetting > saltForkLevel[0].level){
            stylingSaltHandler()
            saltButton = null
        }
        if (waterLevelSetting > sangamonLevel[0].level){
            stylingSangamonHandler()
            sangamonButton = null
        }
        if (waterLevelSetting > salineLevel[0].level){
            stylingSalineHandler()
            salineButton = null
        }

        
        routes = (
        <Container>
                <Row>
                    {routesList.map(route =>{
                        return <Route title={route.name} cost={route.cost} duration={route.duration} waterLevel={route.site} pic={route.img}  />
                    })}
                </Row>
        </Container>)
    } else { routes = null };
    
    
    
    
    return (
        <div>
            {routes}
        </div>
    )
}

export default Routes;
