import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Navbar/navbar';
import MyJumbotron from './Jumbotron/jumbotron';
import Day from './day/day';
import { Container, Row, Col, Button } from 'react-bootstrap';
import moment from 'moment'
import Routes from './routes/routes'
import Prices from './Prices/prices'

class App extends Component {
  state = {
    jumbotron: true,
    showCalendar: false,
    calendar: 1,
    monthN: 'Jan',  
    numOfDays: 31,   
    calendarData: [
      {monthName: 'Jan', numberOfDays: 31, month: 1 ,booking: []},
      {monthName: 'Feb', numberOfDays: 28, month: 2 ,booking: []},
      {monthName: 'Mar', numberOfDays: 31, month: 3 ,booking: []},
      {monthName: 'Apr', numberOfDays: 30, month: 4 ,booking: []},
      {monthName: 'May', numberOfDays: 31, month: 5 ,booking: []},
      {monthName: 'Jun', numberOfDays: 30, month: 6 ,booking: []},
      {monthName: 'Jul', numberOfDays: 31, month: 7 ,booking: []},
      {monthName: 'Aug', numberOfDays: 31, month: 8 ,booking: []},
      {monthName: 'Sep', numberOfDays: 30, month: 9 ,booking: []},
      {monthName: 'Oct', numberOfDays: 31, month: 10,booking: []},
      {monthName: 'Nov', numberOfDays: 30, month: 11,booking: []},
      {monthName: 'Dec', numberOfDays: 31, month: 12,booking: []},
    ]
  }

  hidJumbotronhandler = () => {
    this.setState({
      jumbotron: !this.state.jumbotron
    })
  }

  renderingDate = (currentMonth) => {
    this.state.calendarData.forEach(month => {
      if(month.month === currentMonth){
        this.state.monthN = month.monthName
        this.state.numOfDays = month.numberOfDays
      }
    });
  }

  selectedHandler = () => {
    console.log('clicked')
  }

  togglePrevHandler = () => {
    const currentMonth = this.state.calendar - 1
    this.setState({calendar: currentMonth})
  }

  toggleNextHandler = () => {
    const currentMonth = this.state.calendar + 1
    this.setState({calendar: currentMonth})
  }

  render(){

    if (this.state.calendar > 12){
      this.state.calendar = 1
    } 
    if (this.state.calendar < 1){
      this.state.calendar = 12
    }

    let jumbo = null

    this.renderingDate(this.state.calendar)

    if (this.state.jumbotron){
      jumbo = (
        <div>
          <MyJumbotron click={this.hidJumbotronhandler}/>
          <Routes/>
        </div>
      );
    } else {
      jumbo = (
        <Container>
          <Row>
            <Col><p>date: {moment().format("MMM Do YY")}</p></Col>
            <Col>
              <h4>Calendar</h4>
            </Col>
            <Col>
              <p onClick={this.hidJumbotronhandler} >{'<-back'}</p>
            </Col>
          </Row>
          <Row>
            <Col></Col>
            <Col>
              <Button onClick={this.togglePrevHandler} >prev</Button>
              <span className="date">{this.state.monthN}</span>
              <Button onClick={this.toggleNextHandler} >next</Button>
            </Col>
            <Col></Col>
          </Row>
          <Row>
            <Day  click={this.selectedHandler} num={1}/>
            <Day  click={this.selectedHandler} num={2}/>
            <Day  click={this.selectedHandler} num={3}/>
            <Day  click={this.selectedHandler} num={4}/>
            <Day  click={this.selectedHandler} num={5}/>
            <Day  click={this.selectedHandler} num={6}/>
            <Day  click={this.selectedHandler} num={7}/>
          </Row>
          <Row>
            <Day  click={this.selectedHandler} num={8}/>
            <Day  click={this.selectedHandler} num={9}/>
            <Day  click={this.selectedHandler} num={10}/>
            <Day  click={this.selectedHandler} num={11}/>
            <Day  click={this.selectedHandler} num={12}/>
            <Day  click={this.selectedHandler} num={13}/>
            <Day  click={this.selectedHandler} num={14}/>
          </Row>
          <Row>
            <Day  click={this.selectedHandler} num={15}/>
            <Day  click={this.selectedHandler} num={16}/>
            <Day  click={this.selectedHandler} num={17}/>
            <Day  click={this.selectedHandler} num={18}/>
            <Day  click={this.selectedHandler} num={19}/>
            <Day  click={this.selectedHandler} num={20}/>
            <Day  click={this.selectedHandler} num={21}/>
          </Row>
          <Row>
            <Day  click={this.selectedHandler} num={22}/>
            <Day  click={this.selectedHandler} num={23}/>
            <Day  click={this.selectedHandler} num={24}/>
            <Day  click={this.selectedHandler} num={25}/>
            <Day  click={this.selectedHandler} num={26}/>
            <Day  click={this.selectedHandler} num={27}/>
            <Day  click={this.selectedHandler} num={28}/>
          </Row>
          <Row>
            <Day  click={this.selectedHandler} num={29}/>
            <Day  click={this.selectedHandler} num={30}/>
            <Day  click={this.selectedHandler} num={null}/>
            <Day  click={this.selectedHandler} num={null}/>
            <Day  click={this.selectedHandler} num={null}/>
            <Day  click={this.selectedHandler} num={null}/>
            <Day  click={this.selectedHandler} num={null}/>
          </Row>
        </Container>
      )
    }


    return (
      <div>
        <MyNavbar booking={this.hidJumbotronhandler}/>
        {jumbo}
      </div>
    );
  }
}

export default App;
