import React, {useState, useEffect} from 'react';
import './calendar.css';
import buildCalendar from './build'
// import dayStyles, {beforeToday} from './styles'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Container, Button, Row, Col } from 'react-bootstrap';

export default function Calendar({value, onChange, form, back, booked}) {
    const [calendar , setCalendar] = useState([]);


    useEffect(() => {
        setCalendar(buildCalendar(value));
    }, [value])

    function currMonthName() {
        return value.format('MMMM')
    }

    function currYear() {
        return value.format('YYYY')
    }

    function prevMonth() {
        return value.clone().subtract(1, 'month')
    }

    function nextMonth() {
        return value.clone().add(1, 'month')
    }

    function thisMonth() {
        return value.isSame(new Date(), 'month')
    }


    //styling
    function isSelected(day, value) {
        return value.isSame(day, 'day');
    }
    function beforeToday(day) {
        return day.isBefore(new Date(), 'day');
    }
    function isToday(day) {
        return day.isSame(new Date(), 'day');
    }
    function isBooked(day) {
        return day.isSame(booked, 'day') || day.isSame('2020-11-22', 'day') 
    }
    function dayStyles(day, value) {
        if (beforeToday(day)) return 'before'
        if (isSelected(day, value)) return 'red'
        if (isToday(day)) return 'today'
        if (isBooked(day)) return 'kayaks-2'

        booked.forEach(bookedDate => {
            if (day.isSame(bookedDate, 'day')) {
                return 'kayaks-2'
            }
        })
       
        return ''
    }





    return ( 
        <div className='calendar'>
            <div className='header'>
                <div className='previous' onClick={() => !thisMonth() && onChange(prevMonth())}>
                {!thisMonth() ? String.fromCharCode(171) : null}</div>
                <div className='current'>
                    {currMonthName()} {currYear()}
                </div>
                <div className='next' onClick={() => {onChange(nextMonth())}}>{String.fromCharCode(187)}</div>
            </div>
            <div className='body'>
                <div className="day-names">
                    {
                        ["s","m","t","w","t","f","s"].map(d => <div className='week'>{d}</div>)
                    }
                </div>
                {calendar.map((week) => (
                    <div>
                        {week.map((day) => (
                            <div  className='day' id={day.format("D")} key={day} 
                            onClick={() => { !beforeToday(day) && onChange(day)
                                // console.log('clicked', day)
                                }}>
                                <div className={dayStyles(day, value)}>
                                        {day.format('D').toString()}
                                </div>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
            <div>
                <Container>
                    <Row>
                        <Col sm={4}>
                            <div onClick={back}>
                                <FontAwesomeIcon icon="arrow-left"  size="1x" /> Back
                            </div>
                        </Col>
                        <Col>
                            <div>
                                <p>{value.format("MM/DD")}</p>
                            </div>
                        </Col>
                        <Col sm={4} >
                            <div onClick={form}>
                            Continue  <FontAwesomeIcon icon="arrow-right"  size="1x" />
                            </div>
                        </Col>
                        <Col></Col>
                    </Row>
                </Container>
            </div>
        </div>
    );
}
