import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Navbar/navbar';
import MyJumbotron from './Jumbotron/jumbotron';
import Day from './day/day';
import { Container, Row, Col, Button, Jumbotron } from 'react-bootstrap';
import moment from 'moment'
import Routes from './routes/routes'
import Prices from './Prices/prices'
import Pickup from './Pickup/pickup'
import Calendar from './calendar';

const value = moment()

class App extends Component {
  state = {
    jumbotron: true,
    showCalendar: false,
    showPrice: true,
    showRoutes: true,
    showPickup: true,
  }


  showCalendarHandler = () => {
    this.setState({
      jumbotron: false,
      showCalendar: true,
      showPrice: false,
      showRoutes: false,
      showPickup: false,
    })
  }

  showPriceHandler = () => {
    this.setState({
      showPrice: true,
      showCalendar: false,
      jumbotron: false,
      showRoutes: false,
      showPickup: false,
    })
  }

  showRoutesHandler = () => {
    this.setState({
      showRoutes: true,
      showPrice: false,
      showCalendar: false,
      jumbotron: false,
      showPickup: false
    })
  }

  showHomeHandler = () => {
    this.setState({
      showRoutes: true,
      showPrice: true,
      showCalendar: false,
      jumbotron: true,
      showPickup: true
    })
  }


  render(){

    if (this.state.calendar > 12){
      this.state.calendar = 1
    } 
    if (this.state.calendar < 1){
      this.state.calendar = 12
    }

    let jumbo = null
    let price = null
    let routes = null
    let calendar = null
    let pickup = null



    if (this.state.jumbotron){
      jumbo = ( <MyJumbotron click={this.showCalendarHandler}/>);
    } else { jumbo = (null) }

    if (this.state.showPrice){
      price = (<Prices/>)
    } else { price = null }

    if (this.state.showCalendar){
      calendar = (
        <div>
          <Row>
            <Col>1</Col>
          </Row>
          <Calendar/>
        </div>
      )
    } else { calendar = null }

    if (this.state.showRoutes){
      routes = (<Routes/>)
    } else { routes = null }

    if (this.state.showPickup){
      pickup = (<Pickup/>)
    } else { pickup = null }


    return (
      <div>
        <MyNavbar showCalendar={this.showCalendarHandler}
          showPrice={this.showPriceHandler}
          showRoutes={this.showRoutesHandler}
          showHome={this.showHomeHandler}
        />
        {jumbo}
        <br></br>
        {calendar}
        <br></br>
        {routes}
        <br></br>
        {price}
        <br></br>


        <footer className="footer mt-auto py-3">
          <Container>
            <span className="text-muted">Copyright&#169; 2021 KingFisher Kayaking</span>
          </Container>         
        </footer>
      </div>
    );
  }
}

export default App;
