import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Navbar/navbar'
import MyJumbotron from './Jumbotron/jumbotron'
import Day from './day/day'

class App extends Component {
  state = {
    persons: [
      {name: 'jaems', age: 12},
      {name: 'jaesdfms', age: 122}
    ],
    jumbotron: true
  }

  hidJumbotronhandler = () => {
    this.setState({
      persons: [
        {name: 'jaems', age: 12},
        {name: 'jaesdfms', age: 122}
      ],
      jumbotron: !this.state.jumbotron
    })
  }

  render(){

    let jumbo = null

    if (this.state.jumbotron){
      jumbo = (
        <div>
          <MyJumbotron click={this.hidJumbotronhandler}/>
        </div>
      );
    } else {
      jumbo = (
        <Container>
          <Row>
            <Col>        
              <div>
                <h1>this is the calendar</h1>
                <Day/>
                <button onClick={this.hidJumbotronhandler} >back</button>
              </div>
            </Col>
          </Row>
        </Container>
      )
    }


    return (
      <div>
        <MyNavbar/>
        {jumbo}
      </div>
    );
  }
}

export default App;
