import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Navbar/navbar';
import MyJumbotron from './Jumbotron/jumbotron';
import Day from './day/day';
import { Container, Row, Col, Button, Jumbotron } from 'react-bootstrap';
import moment from 'moment'
import Routes from './routes/routes'
import Prices from './Prices/prices'
import Pickup from './Pickup/pickup'
import Calendar from './calendar';

const value = moment()

class App extends Component {
  state = {
    jumbotron: true,
    showCalendar: false,
    showPrice: false,
    showRoutes: false,
    showPickup: false,
  }


  hidJumbotronhandler = () => {
    this.setState({
      jumbotron: !this.state.jumbotron
    })
  }

  showPriceHandler = () => {
    this.setState({
      showPrice: true,
      showCalendar: false,
      jumbotron: false
    })
  }

  showRoutesHandler = () => {
    this.setState({
      showRoutes: true,
      showPrice: false,
      showCalendar: false,
      jumbotron: false
    })
  }

  togglePrevHandler = () => {
    console.log('prev')
  }

  toggleNextHandler = () => {
    console.log('next')
  }

  render(){

    if (this.state.calendar > 12){
      this.state.calendar = 1
    } 
    if (this.state.calendar < 1){
      this.state.calendar = 12
    }

    let jumbo = null
    let price = null
    let routes = null



    if (this.state.jumbotron){
      jumbo = ( <MyJumbotron click={this.hidJumbotronhandler}/>);
    } else {
      jumbo = (
        <Calendar/>
      )
    }

    if (this.state.showPrice){
      price = (<Prices/>)
    } else {
      price = null
    }


    return (
      <div>
        <MyNavbar booking={this.hidJumbotronhandler}
          showPrice={this.showPriceHandler}
        />
        {jumbo}
        <br></br>
        <br></br>
        {price}
        <br></br>
        <MyJumbotron click={this.hidJumbotronhandler}/>
          <br></br>
        <Routes/>
          <br></br>
          <br></br>
        <Pickup/>

        <footer className="footer mt-auto py-3">
          <Container>
            <span className="text-muted">Copyright&#169; 2021 KingFisher Kayaking</span>
          </Container>         
        </footer>
      </div>
    );
  }
}

export default App;
