import React,{useState} from 'react'
import { Form, Col, Container, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import firebase from 'firebase';
import moment from 'moment';
import './SelectionForm.css';

function SelectionFrom({showForm, kayaks, route, value, showHome, kayaksInStock  }) {

    const [tripName, SetTripName] = useState('no trip name');
    const [kayaksTaking, setKayaksTaking] = useState();
    const [startTime, setTime] = useState('not selected');
    const [location, setLocation] = useState('not selected');
    const [otherLoaction, SetOtherLocation] = useState('not selected');
    const [phoneNum, setPhone] = useState('not selected');
    const [ifChecked, setChecked] = useState();


  // firebase requirements
  const database = firebase.database();
  const ref = database.ref('bookings');


  const booking = {
    route: route,
    name: tripName,
    numOfKayaks: kayaksTaking,
    time: startTime,
    pickUpLocation: location,
    other: otherLoaction,
    date: value.format("MM/DD/YY"),
    phone: phoneNum,
    timeBooked: moment().format('MMMM Do YYYY, h:mm:ss a')
  }

  //submitting form to firebase database
  const submitHandler = () => {
    if(kayaksTaking > 0 && ifChecked){
      console.log('booking sent');
      ref.push(booking);
    //   showHome()
    } else {
      console.log('erroror')
      console.log(ifChecked)
    }
  }



// dynamic Kayaks Option
  let kayaksOptions = null
  let renderKayaksOptions = []
  for (let i = 0; i < kayaksInStock + 1; i++){
    renderKayaksOptions.push(i)
    kayaksOptions = renderKayaksOptions.map(x=> (<option>{x}</option>))
  }

    // booked date from firebase
    kayaks.forEach(booking => {
        if (value.isSame(booking.date, 'day') ) {
            console.log(numOfKayaks)
        }
    });


 







    let selectionForm = null
    if (showForm){ selectionForm = ( 
        <Container fluid >
            <Row className="justify-content-md-center">
                <Col sm={8} >
                    <Form  className='form shadow'>
                        <Form.Row>
                            <p className='form-text header' >{route} on {value.format('dddd')}, {value.format("MMMM Do")}</p>
                        </Form.Row>
                        <hr></hr>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridname">
                            <Form.Label className='form-text'>Name</Form.Label>
                            <Form.Control type="text" placeholder="Trip Name" onChange={(e)=> {SetTripName(e.target.value)}} />
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridPassword">
                                <Form.Label className='form-text'>Number of Kayaks</Form.Label>
                                <Form.Control as="select" defaultValue="Choose..."  onChange={(e)=> {setKayaksTaking(e.target.value)}}>
                                {kayaksOptions}
                                </Form.Control>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridPassword">
                                <Form.Label className='form-text'>Pick Up Location</Form.Label>
                                <Form.Control as="select" defaultValue="Choose..."  onChange={(e) => {setLocation(e.target.value)}}>
                                    <option>MarketPlace Mall (North East Corner)</option>
                                    <option>LincolnSquare Mall (South Side)</option>
                                    <option>Savoy Walmart (North East Corner)</option>
                                    <option>Other</option>
                                </Form.Control>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridname">
                            <Form.Label className='form-text'>(If selected) Other</Form.Label>
                            <Form.Control type="text" placeholder="an address" onChange={(e)=> {SetOtherLocation(e.target.value)}} />
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                        <Form.Group as={Col} controlId="formGridPassword">
                                <Form.Label className='form-text'>Start Time</Form.Label>
                                <Form.Control as="select" defaultValue="Choose..."  onChange={(e)=> {setTime(e.target.value)}}>
                                    <option>8:00 am</option>
                                    <option>9:00 am</option>
                                    <option>10:00 am</option>
                                </Form.Control>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridphone">
                            <Form.Label className='form-text'>Phone Number</Form.Label>
                            <Form.Control type="phone" placeholder="217-777-7777" onChange={(e) => {setPhone(e.target.value)}} />
                            </Form.Group>
                        </Form.Row>

                        <Form.Group id="formGridCheckbox" >
                            <Form.Check onChange={(e) => {setChecked(e.target.checked)}}  type="checkbox" label="I agree to sign my life away (click to see details)" />
                        </Form.Group>

                        <Row>
                            <Col>
                                {/* <div onClick={back}>
                                    <FontAwesomeIcon icon="arrow-left"  size="1x" /> Back
                                </div> */}
                            </Col>
                            <Col>
                                <div onClick={submitHandler}>
                                    Continue  <FontAwesomeIcon icon="arrow-right"  size="1x" />
                                </div>
                            </Col>
                        </Row>
                    </Form>
                </Col>
            </Row>
        </Container>
    );} else { selectionForm = (null)  };
    





    return (
        <div>
            {selectionForm}
        </div>
    )
}

export default SelectionFrom;
