import React, {useState, useEffect} from 'react';
import './calendar.css';
import buildCalendar from './build'
// import dayStyles, {beforeToday} from './styles'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Container, Row, Col } from 'react-bootstrap';

export default function Calendar({value, onChange, back, booked}) {
    const [calendar , setCalendar] = useState([]);
    const [availbleKayaks, setAvailbleKayaks] = useState(9)

    useEffect(() => {
        setCalendar(buildCalendar(value));
    }, [value])

    function currMonthName() {
        return value.format('MMMM')
    }

    function currYear() {
        return value.format('YYYY')
    }

    function prevMonth() {
        return value.clone().subtract(1, 'month')
    }

    function nextMonth() {
        return value.clone().add(1, 'month')
    }

    function thisMonth() {
        return value.isSame(new Date(), 'month')
    }






  
    let test = []  
    const reducer = (accumulator, currentValue) => accumulator + currentValue;

    booked.forEach(booking => {
        if (value.isSame(booking.date, 'day') ) {
            let obj = JSON.parse(booking.numOfKayaks)
            test.push(obj)
        } 
    });


    if (test.length > 1){
        let sum = test.reduce(reducer)

        setAvailbleKayaks(9 - sum)

        console.log(avaibleKayaks)
    } 




    //styling
    function isSelected(day, value) {
        return value.isSame(day, 'day');
    }
    function beforeToday(day) {
        return day.isBefore(new Date(), 'day');
    }
    function isToday(day) {
        return day.isSame(new Date(), 'day');
    }


     

        //styling num of kayaks
    function isBooked0(day) {
        let classes = null
        booked.forEach(bookedData => {
            if (day.isSame(bookedData.date, 'day') && bookedData.numOfKayaks >= 9 ) {
                classes = bookedData.date
            }
        })
        return day.isSame(classes, 'day')
    }
    function isBooked2(day) {
        let classes = null
        booked.forEach(bookedData => {
            if (day.isSame(bookedData.date, 'day') && bookedData.numOfKayaks >= 7 ) {
                classes = bookedData.date
            }
        })
        return day.isSame(classes, 'day')
    }
    function isBooked4(day) {
        let classes = null
        booked.forEach(bookedData => {
            if (day.isSame(bookedData.date, 'day') && bookedData.numOfKayaks >= 5 ) {
                classes = bookedData.date
            }
        })
        return day.isSame(classes, 'day')
    }
    function isBooked6(day) {
        let classes = null
        booked.forEach(bookedData => {
            if (day.isSame(bookedData.date, 'day') && bookedData.numOfKayaks >= 3 ) {
                classes = bookedData.date
            }
        })
        return day.isSame(classes, 'day')
    }




    function dayStyles(day, value) {
        if (beforeToday(day)) return 'before'
        if (isSelected(day, value)) return 'red'
        if (isToday(day)) return 'today'
        if (isBooked0(day)) return 'kayaks-0'
        if (isBooked2(day) ) return 'kayaks-2'
        if (isBooked4(day)) return 'kayaks-4'
        if (isBooked6(day)) return 'kayaks-6'
        return ''
    }





    let kayaks = 9

    booked.forEach(bookedData => {
        if (value.isSame(bookedData.date, 'day')){
            kayaks = (9 - bookedData.numOfKayaks)
        }
    })



    return ( 
        <Container>
            <div className='calendar'>
                <div className='header'>
                    <div className='previous' onClick={() => !thisMonth() && onChange(prevMonth())}>
                    {!thisMonth() ? String.fromCharCode(171) : null}</div>
                    <div className='current'>
                        {currMonthName()} {currYear()}
                    </div>
                    <div className='next' onClick={() => {onChange(nextMonth())}}>{String.fromCharCode(187)}</div>
                </div>
                <div className='body'>
                    <div className="day-names">
                        {
                            ["s","m","t","w","t","f","s"].map(d => <div className='week'>{d}</div>)
                        }
                    </div>
                    {calendar.map((week) => (
                        <div>
                            {week.map((day) => (
                                <div  className='day' id={day.format("D")} key={day} 
                                onClick={() => { !beforeToday(day) && onChange(day)
                                    // console.log('clicked', day)
                                    }}>
                                    <div className={dayStyles(day, value)}>
                                            {day.format('D').toString()}
                                    </div>
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
            </div>
            <Container>
                <Row>
                    <Col sm={2}>
                        <div onClick={back}>
                            <FontAwesomeIcon icon="arrow-left" size="1x" /> Back
                        </div>
                    </Col>
                    <Col  sm={7}>
                        <div>
                            <h6 className='selection-footer' >Date Selected: {value.format("MM/DD")}</h6>
                            <h6 className='selection-footer'>Number of Kayaks Avalible: {avaibleKayaks}</h6>
                        </div>
                    </Col>
                    <Col sm={1}></Col>
                    <Col sm={2} >
                        {/* <div onClick={form}>
                        Continue  <FontAwesomeIcon icon="arrow-right"  size="1x" />
                        </div> */}
                    </Col>
                    <Col></Col>
                </Row>
            </Container>
        </Container>
    );
}
