import React, { useEffect, useState } from 'react';
import {  Card, Col, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './newRouteCss.css';



const Route = ({title, waterLevel, duration, cost, learn, pic, setRoute, waterLevelSetting}) => {

    const [waterLevelAPI, setWaterLevel] = useState(null);


    //getting waterlevel data
    const url = `https://waterservices.usgs.gov/nwis/iv/?format=json&sites=0${waterLevel}&parameterCd=00065&siteStatus=all`

    useEffect(() => {
        async function getData() {
            const response = await fetch(url);
            const data = await response.json();
            const waterLevelStat = data.value.timeSeries[0].values[0].value[0].value
            setWaterLevel(waterLevelStat)
        }
        getData();
    },[])


    //rendering waterlevel data and style
    let renderWaterLevel = null
    let boxClass = null
    let clickFunction = null
    if(waterLevelAPI === null) {
        renderWaterLevel = (null)
        boxClass = 'text'
        clickFunction = (
            () => {setRoute(title)}
        )
    }   else {
        if(waterLevelSetting > waterLevelAPI) {
            boxClass = 'too-low'
            clickFunction = (null)
        }else {
            boxClass = 'text'
            clickFunction = (
                () => {setRoute(title)}
            )
        }
        renderWaterLevel = waterLevelAPI
    }




  return (
    <div>
        <ul>
            <li class="booking-card" style="background-image: url(https://images.pexels.com/photos/4586485/pexels-photo-4586485.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940)">
                <div class="book-container">
                <div class="content">
                    <button class="btn">Book</button>
                </div>
                </div>
                <div class="informations-container">
                <h2 class="title">Saline</h2>
                <p class="sub-title">From Crystal Lake to St. Joseph</p>
                <p class="price"><svg class="icon" style="width:24px;height:24px" viewBox="0 0 24 24">
                <path fill="currentColor" d="M3,6H21V18H3V6M12,9A3,3 0 0,1 15,12A3,3 0 0,1 12,15A3,3 0 0,1 9,12A3,3 0 0,1 12,9M7,8A2,2 0 0,1 5,10V14A2,2 0 0,1 7,16H17A2,2 0 0,1 19,14V10A2,2 0 0,1 17,8H7Z" />
                </svg>$ 31</p>
                <p class="info-p">Time: 1hr</p>
                <div class="more-information">
                    <div class="info-and-date-container">
                    <div class="box info">
                        <svg class="icon" style="width:24px;height:24px" viewBox="0 0 24 24">
                <path fill="currentColor" d="M11,9H13V7H11M12,20C7.59,20 4,16.41 4,12C4,7.59 7.59,4 12,4C16.41,4 20,7.59 20,12C20,16.41 16.41,20 12,20M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M11,17H13V11H11V17Z" />
                </svg>
            
                        <p>Learn More</p>
                    </div>
                    <div class="box date">
                        <svg class="icon" style="width:24px;height:24px" viewBox="0 0 24 24">
                <path fill="currentColor" d="M19,19H5V8H19M16,1V3H8V1H6V3H5C3.89,3 3,3.89 3,5V19A2,2 0 0,0 5,21H19A2,2 0 0,0 21,19V5C21,3.89 20.1,3 19,3H18V1M17,12H12V17H17V12Z" />
            </svg>
                        <p>Go to Calendar</p>
                    </div>
                    </div>
                    <p class="disclaimer">disclaimer</p>
                    </div>
                </div>
            </li>
        </ul>
    </div>
  );
};

export default Route;
