import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Navbar/navbar';
import MyJumbotron from './Jumbotron/jumbotron';
import Day from './day/day';
import { Container, Row, Col, Button } from 'react-bootstrap';
import moment from 'moment'
import Routes from './routes/routes'
import Prices from './Prices/prices'
import Pickup from './Pickup/pickup'
import Calendar from './calendar';

const value = moment()

class App extends Component {
  state = {
    jumbotron: false,
    showCalendar: false,
  }


  hidJumbotronhandler = () => {
    this.setState({
      jumbotron: !this.state.jumbotron
    })
  }



  togglePrevHandler = () => {
    console.log('prev')
  }

  toggleNextHandler = () => {
    console.log('next')
  }

  render(){

    if (this.state.calendar > 12){
      this.state.calendar = 1
    } 
    if (this.state.calendar < 1){
      this.state.calendar = 12
    }

    let jumbo = null

    // this.renderingDate(this.state.calendar)

    if (this.state.jumbotron){
      jumbo = (
        <div>
          <MyJumbotron click={this.hidJumbotronhandler}/>
          <br></br>
          <Routes/>
          <br></br>
          <Prices/>
          <br></br>
          <Pickup/>
        </div>
      );
    } else {
      jumbo = (
        <Calendar/>
      )
    }


    return (
      <div>
        <MyNavbar booking={this.hidJumbotronhandler}/>
        {jumbo}
        <br></br>
        <br></br>
        <br></br>

        <footer className="footer mt-auto py-3">
          <Container>
            <span className="text-muted">Copyright&#169; 2021 KingFisher Kayaking</span>
          </Container>         
        </footer>
      </div>
    );
  }
}

export default App;
