import React from 'react';
import {  Card, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';



const weatherDay = ({temp, weather, day}) => {

  let weatherIcon = null
  if (weather === 'Clear'){
      weatherIcon = (<FontAwesomeIcon icon="sun"  size="2x" />)
  } else if (weather === 'Rain'){
    weatherIcon = (
        <div>
            <FontAwesomeIcon icon="cloud-rain"  size="2x" />
            <FontAwesomeIcon icon="smile-beam"  size="2x" />
        </div>)
  } else {
      weatherIcon = (
      <div>
          <FontAwesomeIcon icon="cloud"  size="2x" />
          <FontAwesomeIcon icon="meh"  size="2x" />
      </div>)
  }




  return (
    <Col sm >
        <Card >
            <Card.Body className="card">
                <Card.Text>
                    <h6>{day}</h6>
                    <h6>{temp} &#8457;</h6>
                    {/* <h6>{weather}</h6> */}
                    {weatherIcon}
                </Card.Text>
            </Card.Body>
        </Card>
    </Col>  
  );
};

export default weatherDay;
