import React, {Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import MyNavbar from './Componets/Navbar/navbar';
import MyJumbotron from './Componets/Jumbotron/jumbotron';
import { Container, Row, Col, Button, Jumbotron } from 'react-bootstrap';
import moment from 'moment';
import Routes from './Componets/routes/routes';
import Prices from './Componets/Prices/prices';
import Pickup from './Componets/Pickup/pickup';
import Calendar from './Componets/calendar';
import SelectionForm from './Componets/SelectionForm/SelectionForm'

const value = moment()

class App extends Component {
  state = {
    jumbotron: false,
    showCalendar: false,
    showPrice: false,
    showRoutes: false,
    showPickup: false,
  }


  showCalendarHandler = () => {
    this.setState({
      jumbotron: false,
      showCalendar: true,
      showPrice: false,
      showRoutes: false,
      showPickup: false,
    })
  }

  showPriceHandler = () => {
    this.setState({
      showPrice: true,
      showCalendar: false,
      jumbotron: false,
      showRoutes: false,
      showPickup: false,
    })
  }

  showRoutesHandler = () => {
    this.setState({
      showRoutes: true,
      showPrice: false,
      showCalendar: false,
      jumbotron: false,
      showPickup: false
    })
  }

  showHomeHandler = () => {
    this.setState({
      showRoutes: true,
      showPrice: true,
      showCalendar: false,
      jumbotron: true,
      showPickup: true
    })
  }

  showPickUpHandler = () => {
    this.setState({
      showRoutes: false,
      showPrice: false,
      showCalendar: false,
      jumbotron: false,
      showPickup: true
    })
  }


  render(){

    if (this.state.calendar > 12){
      this.state.calendar = 1
    } 
    if (this.state.calendar < 1){
      this.state.calendar = 12
    }

    let jumbo = null
    let price = null
    let routes = null
    let calendar = null
    let pickup = null



    if (this.state.jumbotron){
      jumbo = ( <MyJumbotron/>);
    } else { jumbo = (null) }

    if (this.state.showPrice){
      price = (<Prices/>)
    } else { price = null }

    if (this.state.showCalendar){
      calendar = (
        <Container>
          <Row>What's Available</Row>
          <Row>
            <Col><p className='kayaks-0'>kayaks = 0</p></Col>
            <Col><p className='kayaks-2'>kayaks > 2</p></Col>
            <Col><p className='kayaks-4'>kayaks > 4</p></Col>
            <Col><p className='kayaks-6'>kayaks > 6</p></Col>
            <Col><p className='kayaks-8'>kayaks > 8</p></Col>
          </Row>
          <Calendar/>
        </Container>
      )
    } else { calendar = null }

    if (this.state.showRoutes){
      routes = (<Routes/>)
    } else { routes = null }

    if (this.state.showPickup){
      pickup = (<Pickup/>)
    } else { pickup = null }


    return (
      <div>
        <MyNavbar showCalendar={this.showCalendarHandler}
          showPrice={this.showPriceHandler}
          showRoutes={this.showRoutesHandler}
          showHome={this.showHomeHandler}
          showPickup={this.showPickUpHandler}
        />
        {/* <SelectionForm/> */}
        {jumbo}
        <br></br>
        {calendar}
        <br></br>
        {routes}
        <br></br>
        {price}
        {pickup}


        <footer className="footer mt-auto py-3">
          <Container>
            <span className="text-muted">Copyright&#169; 2021 KingFisher Kayaking</span>
          </Container>         
        </footer>
      </div>
    );
  }
}

export default App;
