import React, { useState, useEffect, useRef } from 'react';
import { Col, Container, Row, Spinner } from 'react-bootstrap';
import Chartjs from 'chart.js';
import moment from 'moment';

const url7day = 'https://api.openweathermap.org/data/2.5/onecall?lat=40.1105556&lon=-88.2072222&units=imperial&appid=4ccd9d42e589371f8ea88443f33536f9'

const url = 'http://api.openweathermap.org/data/2.5/forecast?q=urbana&appid=4ccd9d42e589371f8ea88443f33536f9&units=imperial'


const Chart = () => {
  const chartContainer = useRef(null);
  const [chartInstance, setChartInstance] = useState(null);

  const [weather, setWeather] = useState([{temp: 1, rain: 'clear'},{temp: 2, rain: 'clear'},{temp: 3, rain: 'clear'},{temp: 4, rain: 'clear'},{temp: 5, rain: 'clear'},{temp: 6, rain: 'clear'},{temp: 7, rain: 'clear'}])

  const now = moment();
  const today = useState(now.format('dddd'))
  const tomorrow = useState(now.add(1, 'days').format('dddd'))
  const twoDaysAway  = useState(now.add(1, 'days').format('dddd'))
  const threeDaysAway  = useState(now.add(1, 'days').format('dddd'))
  const fourDaysAway  = useState(now.add(1, 'days').format('dddd'))
  const fiveDaysAway  = useState(now.add(1, 'days').format('dddd'))
  const sixDaysAway  = useState(now.add(1, 'days').format('dddd'))

  useEffect(() => {
    //fetching data && rendering chart
    async function get() {

        // const response = await fetch(url7day);
        // const data = await response.json();
    
        // console.log(data)  
        // console.log(data.daily[0].temp.day)
        // console.log(data.daily[0].weather[0].main)    
       
        
        //rendering chart


        



    }
    get();

  }, [chartContainer]);


  useEffect(() => {
    


    setTimeout(() => {
        console.log('you can see me after 2 seconds')
        if (chartContainer && chartContainer.current) {
            const newChartInstance = new Chartjs(chartContainer.current, chartConfig);
            setChartInstance(newChartInstance);
        }
        console.log(weather)
    }, 2000);

  }, []);






  const chartConfig = {
    type: 'line',
        data: {
        datasets: [{
            data: [weather[0][0].temp,weather[0][1].temp,weather[0][2].temp,weather[0][3].temp,weather[0][4].temp,weather[0][5].temp,weather[0][6].temp,],
            label: 'Temperture Forcast (C)',
            backgroundColor: '#fde17e',
    
            // This binds the dataset to the left y axis
            yAxisID: 'left-y-axis'
        }, {
            data: [0.10, 0.20, 0.30, 0.30, 0.30, 0, 0.20],
            label: 'Chance of Rain (%)',
            backgroundColor: '#4DC3FF',
    
            // This binds the dataset to the right y axis
            yAxisID: 'right-y-axis'
        }],
        labels: [today[0], tomorrow[0], twoDaysAway[0], threeDaysAway[0], fourDaysAway[0],fiveDaysAway[0],sixDaysAway[0],]
    },
        options: {
            scales: {
                yAxes: [{
                    id: 'left-y-axis',
                    type: 'linear',
                    position: 'left'
                }, {
                    id: 'right-y-axis',
                    type: 'linear',
                    position: 'right'
                }]
            }
        }
    };





  let renderChart = null
  if (true) {
      renderChart = (
        <Row>
            <Col>
                <canvas ref={chartContainer} />
            </Col>
        </Row>
      )
    } 


  return (
    <Container>
        <Row>
            <Col sm={4} ></Col>
            <Col>
                <h4>Weather App</h4>
            </Col>
        </Row>
        {renderChart}
    </Container>
  );
};

export default Chart;
